DIV.SCRIPT:
function reviewHistPingAjax() {
  jQuery.get("/gp/customer-reviews/common/du/recordHistoPopAjax.html", null);
}

var reviewHistPopoverConfig = {
  showOnHover:true,
  showCloseButton: false,
  width:null,
  location:'bottom',
  locationAlign:'left',
  locationOffset:[-20,0],
  group: 'reviewsPopover',
  clone:false,
  hoverHideDelay:300
};

amznJQ.onReady('popover', function() {
  jQuery('.asinReviewsSummary').each(
    function() {
      if (typeof jQuery(this).removeAmazonPopoverTrigger === 'function' && typeof jQuery(this).amazonPopoverTrigger === 'function') {
        jQuery(this).removeAmazonPopoverTrigger();
        var asin = jQuery(this).attr('name');
        var ref = jQuery(this).attr('ref');
        var getargs = jQuery(this).attr('getargs');

        var remoteContentAddr =
          '/gp/customer-reviews/common/du/displayHistoPopAjax.html?' +
          '&ASIN=' + asin +
          '&link=1' +
          '&seeall=1' + 
          '&ref=' + ref;
        if (typeof getargs != 'undefined') {
            remoteContentAddr += '&getargs=' + getargs;
        }

        var myConfig = jQuery.extend(true, {}, reviewHistPopoverConfig);
        myConfig.destination = remoteContentAddr;
        jQuery(this).amazonPopoverTrigger(myConfig);
      }
    }
  );
});

if (amznJQ && amznJQ.onReady)
{
  amznJQ.onReady("JQuery", function()
  {
    jQuery("td.tpType").parent("tr").hover(
      function() { jQuery(this).addClass("toeRowHover"); },
      function() { jQuery(this).removeClass("toeRowHover"); });
  });
}

amznJQ.onReady("JQuery", function() {
  amznJQ.available("ImageRotation" , function () {
     setupImageRotation("atf");
     setupImageRotation("btf");
  });
});
